INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SØ_2', '{"kode": "RV_SØ_2", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "d0a7da23-0e4e-44ed-87f2-cc3a090e50e0", "tittel": "Minst én dag er avslått på grunn av foreldelse. Vurder å sende vedtaksbrev fra Infotrygd", "avviklet": false, "handling": "Hvis det ikke finnes noe unntak fra foreldelsesfristen skal du godkjenne i Speil og sende vedtaksbrev fra Infotrygd. Hvis forslaget er feil må du avvise saken i Speil og behandle i Infotrygd. Dersom dagen/dagene som er avslått er innenfor arbeidsgiverperioden trenger du ikke å sende avslag fra Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Søknaden har kommet inn mer enn 3 måneder før dagen/dagene.", "varselkode": "RV_SØ_2"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SØ_3', '{"kode": "RV_SØ_3", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "117db82f-deb9-4ab4-a46f-df4024212cb0", "tittel": "Sykmeldingen er tilbakedatert, vurder fra og med dato for utbetaling.", "avviklet": false, "handling": "Hvis tilbakedateringen er godkjent, skal du finne notat i Gosys eller stå i fritekstfelt på SP-UB. Da kan du godkjenne saken i Speil. Hvis tilbakedateringen er under vurdering, skal det være en åpen Gosys-oppgave. I slike tilfeller skal du legge saken på vent til vurderingen er ferdig. Hvis tilbakedateringen er avslått, skal du avvise saken i Speil og sende vedtak. Registerer avslaget i SP-SA. Husk også å lukke oppgaven på søknaden/inntektsmeldingen i Gosys.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Sykmeldingen er tilbakedatert.", "varselkode": "RV_SØ_3"}]}') ON CONFLICT(varselkode) DO NOTHING;
