INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SØ_8', '{"kode": "RV_SØ_8", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "bec32dcc-a24e-4069-925b-0496bb70b4d7", "tittel": "Utenlandsopphold oppgitt i perioden i søknaden.", "avviklet": false, "handling": "Undersøk om det er vedtak om beholde sykepenger under utenlandsopphold. Hvis det er søkt uten at det er behandlet - legg saken på vent til det foreligger vedtak i saken. Korriger dager dersom det er gitt avslag - og godkjenn saken i ny løsning. Hvis brukeren har fått vedtak fra NAV-kontoret, trenger du ikke å sende nytt vedtak.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Det er oppgitt utenlandsopphold utenfor EØS i søknaden", "varselkode": "RV_SØ_8"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SØ_9', '{"kode": "RV_SØ_9", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "5dde82cc-cad5-41f2-8476-e22e1ca2bdb9", "tittel": "Det er oppgitt annen inntektskilde i søknaden. Vurder inntekt.", "avviklet": true, "handling": "Sjekk hva den andre inntektskilden består av og vurder om saken kan behandles i Speil. Hvis saken ikke kan behandles i Speil, må du behandle saken i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Den sykmeldte har oppgitt «Annet» som annen inntektskilde i søknaden.", "varselkode": "RV_SØ_9"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SØ_26', '{"kode": "RV_SØ_26", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "c9840dcd-6e3f-4d87-96ab-8e287d005e6c", "tittel": "Søknaden inneholder Feriedager utenfor perioden søknaden gjelder for", "avviklet": true, "handling": "Undersøk om perioden blir riktig beregnet.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "I søknadsdialogen så blir den sykmeldte spurt om det er ferie i mellomliggende periode. Dette for å finne ut om to perioder hører sammen og om man trenger inntektsmelding.", "varselkode": "RV_SØ_26"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SØ_10', '{"kode": "RV_SØ_10", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "2e73a8ac-3cc1-4a79-bf62-309412b54339", "tittel": "Den sykmeldte har fått et nytt inntektsforhold.", "avviklet": false, "handling": "Du må sjekke om sykepengene skal graderes ut ifra inntekt i det nye inntektsforholdet. Du må finne informasjon om inntekten og når den er opptjent. Ta stilling til om det bør sendes en oppgave til NAV-kontoret for vurdering av vilkårene i § 8-4.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Den sykmeldte har oppgitt i søknaden at han/hun har fått et nytt inntektsforhold i sykmeldingsperioden.", "varselkode": "RV_SØ_10"}]}') ON CONFLICT(varselkode) DO NOTHING;
