INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_OO_1', '{"kode": "RV_OO_1", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "3b295b53-cd74-46c8-b474-42f20adc3dc0", "tittel": "Det er behandlet en søknad i Speil for en senere periode enn denne.", "avviklet": false, "handling": "Undersøk at behandlingen av perioden blir riktig.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Søknadene har ikke kommet i vanlig rekkefølge. Dette kan skje dersom den sykmeldte avbryter en søknad/sykmelding, eller en lege skriver ut en tilbakedatert sykmelding. Perioder som allerede er avsluttet vil revurderes når denne perioden godkjennes.", "varselkode": "RV_OO_1"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_OO_2', '{"kode": "RV_OO_2", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "c61058eb-73dc-4c28-9781-8ec492dbfc17", "tittel": "Saken må revurderes fordi det har blitt behandlet en tidligere periode som kan ha betydning.", "avviklet": false, "handling": "Sjekk at telling av forbrukte dager og/eller opptjening av ny rett er riktig beregnet. Dersom revurderingen gjør at det er for mye utbetalt til den sykmeldte, se egne rutiner for håndtering av feilutbetalinger.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Det har blitt behandlet en søknadsperiode som gjelder et tidligere tidsrom. Dette kan medføre at antall forbrukte dager er endret eller at opptjening av ny rett til sykepenger påvirkes.", "varselkode": "RV_OO_2"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IM_1', '{"kode": "RV_IM_1", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "abdfd599-ac5f-4f61-922a-bf62e2074096", "tittel": "Vi har mottatt en inntektsmelding i en løpende sykmeldingsperiode med oppgitt første/bestemmende fraværsdag som er ulik tidligere fastsatt skjæringstidspunkt.", "avviklet": false, "handling": "Undersøk om arbeidet er gjenopptatt slik at sykmeldingen avbrytes. Det må avklares om det blir et nytt skjæringstidspunkt. Hvis det blir nytt skjæringstidspunkt, må perioden med det nye skjæringstidspunktet avvises i vedtaksløsningen og saken må behandles i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Det kan bety at den sykmeldte har gjenopptatt arbeidet og så blitt sykmeldt igjen. Det kan derfor være et nytt skjæringstidspunkt.", "varselkode": "RV_IM_1"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IM_2', '{"kode": "RV_IM_2", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "641acb03-c373-4760-816a-26999ac7cd3a", "tittel": "Første fraværsdag i inntektsmeldingen er ulik skjæringstidspunktet. Kontrollér at inntektsmeldingen er knyttet til riktig periode.", "avviklet": false, "handling": "Kontroller at skjæringstidspunktet er riktig.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Første fraværsdag i inntektsmeldingen er ulik første fraværsdag i sykdomsperioden:", "varselkode": "RV_IM_2"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IM_3', '{"kode": "RV_IM_3", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "e2d1cd1e-ae3e-4305-bfa6-c1c7825bc000", "tittel": "Inntektsmeldingen og vedtaksløsningen er uenige om beregningen av arbeidsgiverperioden. Undersøk hva som er riktig arbeidsgiverperiode.", "avviklet": false, "handling": "Vurder hva som er riktig arbeidsgiverperiode. Hvis forslaget til vedtaksløsningen er feil, må saken behandles i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Arbeidsgiver og vedtaksløsningen har beregnet arbeidsgiverperioden ulikt.", "varselkode": "RV_IM_3"}]}') ON CONFLICT(varselkode) DO NOTHING;
