INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IM_14', '{"kode": "RV_IM_14", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "5070abe4-3296-420e-857e-6771972d47a8", "tittel": "Første fraværsdag oppgitt i inntektsmeldingen er ulik den systemet har beregnet. Utbetal kun hvis dagsatsen er korrekt", "avviklet": true, "handling": "Sjekk om dagsatsen er riktig, og om saken faktisk er under 25 prosent avvik med sammenligningsgrunnlaget.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Det er forskjell mellom det arbeidsgiver har oppgitt som første fraværsdag og sykmeldingens fra og med-dato.", "varselkode": "RV_IM_14"}]}') ON CONFLICT(varselkode) DO NOTHING;
