INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IM_18', '{"kode": "RV_IM_18", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "21019224-6d72-4ab0-a556-591aff2bb909", "tittel": "Inntektsmeldingen mangler arbeidsgiverperiode. Vurder om vilkårene for sykepenger er oppfylt, og om det skal være arbeidsgiverperiode", "avviklet": true, "handling": "Kontrollér at det kun er én arbeidsgiver. Skal kun komme ved flere arbeidsforhold i samme virksomhet.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Selvforklarende. Oppstår dersom inntektsmelding kommer inn for et arbeidsforhold bruker ikke er sykmeldt i.", "varselkode": "RV_IM_18"}]}') ON CONFLICT(varselkode) DO NOTHING;
