INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_RE_1', '{"kode": "RV_RE_1", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "dafa1312-a809-46fc-9d04-3ed59abda4a3", "tittel": "Fant ikke refusjonsgrad for perioden. Undersøk oppgitt refusjon før du utbetaler.", "avviklet": false, "handling": "Undersøk hva som er oppgitt i siste inntektsmelding og eventuell informasjon i Infotrygd. Dersom refusjonen er satt feil må perioden behandles i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Fant ikke informasjon om det er refusjon i perioden. Refusjonen er satt til 100 % siden det samsvarer med siste utbetalte periode i Infotrygd.", "varselkode": "RV_RE_1"}]}') ON CONFLICT(varselkode) DO NOTHING;
