INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IT_29', '{"kode": "RV_IT_29", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "240f98eb-5b2f-42dd-bfc3-2204672ef5af", "tittel": "Perioden er lagt inn i Infotrygd - men mangler inntektsopplysninger. Fjern perioden fra SP UB hvis du utbetaler via speil.", "avviklet": true, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_IT_29"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_12', '{"kode": "RV_VV_12", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "fc405fad-382a-44c1-a058-03e56bacbede", "tittel": "Kan ikke overstyre inntekt uten at det foreligger et vilkårsgrunnlag", "avviklet": false, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_VV_12"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IT_30', '{"kode": "RV_IT_30", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "bd32e503-c9c6-440f-8225-6bfa86fce3c3", "tittel": "Perioden er lagt inn i Infotrygd, men ikke utbetalt. Fjern fra Infotrygd hvis det utbetales via speil.", "avviklet": true, "handling": "Dette varselet betyr ikke at perioden må behandles i Infotrygd. Fjern perioden fra SP UB når du utbetaler via speil. Hvis inntekten er registert så kan den slettes på SP-VT. Hvis det er en forutgående periode i Infotrygd som enda ikke er behandlet, trenger du ikke å avvise perioden hvis det er flere sykepengedager igjen er enn det som ligger til behandling i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Det ligger registrert en søknad om sykepenger på SP UB.", "varselkode": "RV_IT_30"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IT_31', '{"kode": "RV_IT_31", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "2358c6fa-4b35-4057-b773-e76ad1217c44", "tittel": "Perioden forlenger en behandling i Infotrygd, og har historikk fra ny løsning: Undersøk at antall dager igjen er beregnet riktig.", "avviklet": true, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_IT_31"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IT_32', '{"kode": "RV_IT_32", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "304771db-3414-4d34-be57-5592cb456ec0", "tittel": "Perioden forlenger en behandling i ny løsning, og har historikk i Infotrygd også: Undersøk at antall dager igjen er beregnet riktig.", "avviklet": true, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_IT_32"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IT_33', '{"kode": "RV_IT_33", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "1b706fc0-190e-46dd-9e52-4cd11bcaed4e", "tittel": "Skjæringstidspunktet har endret seg som følge av historikk fra Infotrygd", "avviklet": false, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_IT_33"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_1', '{"kode": "RV_VV_1", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "2ddb5e8d-a9fb-46c0-b176-1886ba086323", "tittel": "Arbeidsgiver er ikke registrert i Aa-registeret.", "avviklet": false, "handling": "Sjekk at bruker er ansatt hos arbeidsgiveren.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Arbeidsgiver på sykmelding eller inntektsmelding er ikke registrert i Aa-registeret.", "varselkode": "RV_VV_1"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_2', '{"kode": "RV_VV_2", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "315a8cb9-8b62-4fbb-b8d3-32666a0fcda2", "tittel": "Flere arbeidsgivere, ulikt starttidspunkt for sykefraværet eller ikke fravær fra alle arbeidsforhold", "avviklet": false, "handling": "Du må fastsette et sykepengegrunnlag basert på inntekt fra alle arbeidsforholdene. Du må vurdere om de foreslåtte arbeidsforholdene er reelle og skal tas med i beregningen.  Hvis det er kommet inntektsmelding fra arbeidsgiver med senere starttidspunkt, må det sjekkes om inntekten for dette arbeidsforholdet er riktig beregnet (§8-28). Hvis det ikke er kommet inntektsmelding fra denne arbeidsgiveren, må den foreslåtte inntekten vurderes. Du må vurdere om den innhentede inntekten er riktig. Sjekk om det er et nytt arbeidsforhold eller varig lønnsendring i beregningsperioden. Sjekk om det variasjoner i inntekten som kan bety at det er lovlig fravær uten lønn i beregningsperioden. Vurderes etter §8-28.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Det tidligste sykefraværet bestemmer skjæringstidspunktet. Dette gjelder: Inntekt på inntektsmeldingen som kommer fra arbeidsgivere med senere starttidspunkt kan gjelde feil periode og/eller inntekten som er foreslått for minst et av arbeidsforholdene er innhentet fra a-ordningen og er gjennomsnittet av rapportert inntekt de tre siste månedene før tidspunktet for arbeidsuførhet (§8-28 3. ledd bokstav a).", "varselkode": "RV_VV_2"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_3', '{"kode": "RV_VV_3", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "f95d3440-b1a3-4bc4-9a92-893a7e0bca4a", "tittel": "Første utbetalingsdag er i Infotrygd og mellom 1. og 16. mai. Kontroller at riktig grunnbeløp er brukt.", "avviklet": true, "handling": "Kontroller at dagsatsen samsvarer med grunnbeløpet som skal brukes.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Vi klarer ikke å lese riktig skjæringstidspunkt når saken har startet i Infotrygd. Vi må kontrollere at riktig grunnbeløp er brukt i saker med sykepengegrunnlag over 6G.", "varselkode": "RV_VV_3"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_4', '{"kode": "RV_VV_4", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "ee56b459-bc4b-4c99-9355-1339a802e71f", "tittel": "Minst én dag uten utbetaling på grunn av sykdomsgrad under 20 %. Vurder å sende vedtaksbrev fra Infotrygd", "avviklet": false, "handling": "Dersom du er enig i vurderingen, send avslagsbrev fra Infotrygd og godkjenn i ny løsning. OBS!!: Hør med den som er sykmeldt hvis det er mistanke om misforståelser ved utfylling av søknaden. Da kan du sende spørsmål i Modia og legge saken på vent til du får svar.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Minst én dag uten utbetaling på grunn av sykdomsgrad under 20", "varselkode": "RV_VV_4"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_5', '{"kode": "RV_VV_5", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "ccce5bcf-3b3a-4be7-b45a-078cd841861d", "tittel": "Bruker mangler nødvendig inntekt ved validering av Vilkårsgrunnlag", "avviklet": false, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_VV_5"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_8', '{"kode": "RV_VV_8", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "21e85c69-9f5d-46db-af29-b9868682c635", "tittel": "Den sykmeldte har skiftet arbeidsgiver, og det er beregnet at den nye arbeidsgiveren mottar refusjon lik forrige. Kontroller at dagsatsen blir riktig.", "avviklet": false, "handling": "Sjekk at riktig dagsats er lagt til grunn.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Den sykmeldte har skiftet arbeidsgiver i løpet av sykefraværet.", "varselkode": "RV_VV_8"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_9', '{"kode": "RV_VV_9", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "85da2c4a-7ac0-49d1-8827-0c28fd4fbb03", "tittel": "Bruker er fortsatt syk 26 uker etter maksdato", "avviklet": false, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_VV_9"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_10', '{"kode": "RV_VV_10", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "9c6c1493-bfcc-401b-94cb-4f43553c8300", "tittel": "Fant ikke vilkårsgrunnlag. Kan ikke vilkårsprøve på nytt etter ny informasjon fra saksbehandler.", "avviklet": false, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_VV_10"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_13', '{"kode": "RV_VV_13", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "38d235be-490f-45c0-9beb-03b03807cf03", "tittel": "26 uker siden forrige utbetaling av sykepenger, vurder om vilkårene for sykepenger er oppfylt", "avviklet": true, "handling": "Saken må behandles som en førstegangssak og alle vilkårene for å få sykepenger må vurderes.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Bruker har tidligere fått utbetalt sykepenger til maksdato. Det er nå mer enn 26 uker siden siste utbetaling av sykepenger(§ 8-12).", "varselkode": "RV_VV_13"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_14', '{"kode": "RV_VV_14", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "2c000f04-9f6f-44b1-be7e-69ac399b1a7b", "tittel": "Denne personen har en utbetaling for samme periode for en annen arbeidsgiver. Kontroller at beregningene for begge arbeidsgiverne er korrekte.", "avviklet": true, "handling": "Det kan hende at begge beregningene er riktig, men dette må kontrolleres. Hvis det er feil må perioder som er ferdig behandlet annulleres og alt må behandles i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Da vi beregnet den forrige utbetalingen visste vi ikke at personen var syk for denne arbeidsgiveren.", "varselkode": "RV_VV_14"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_15', '{"kode": "RV_VV_15", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "16f8f721-5867-43fb-b4fa-2ef135b9b405", "tittel": "Maks antall sykepengedager er nådd i perioden", "avviklet": true, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_VV_15"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_VV_16', '{"kode": "RV_VV_16", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "c339ad96-9160-450d-a2a8-75ca4f526edd", "tittel": "Søknaden inneholder egenmeldingsdager som ikke er oppgitt i inntektsmeldingen. Vurder om arbeidsgiverperioden beregnes riktig", "avviklet": true, "handling": "Sjekk om arbeidsgiver har oppgitt riktig arbeidsgiverperioden.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Egenmeldingsdager er ikke oppgitt i inntektsmeldingen, men i søknaden", "varselkode": "RV_VV_16"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_OV_1', '{"kode": "RV_OV_1", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "fb5d7e8d-315f-4ad9-8706-e6ee39f0afe6", "tittel": "Perioden er avslått på grunn av manglende opptjening", "avviklet": false, "handling": "Undersøk at opptjeningen er vurdert riktig. Hvis opptjening ikke er oppfylt kan saken godkjennes i speil og det må sendes avslagsbrev i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Alle dager er avvist på grunn av at personen ikke har jobbet mer enn fire uker før sykmeldingstidspunktet", "varselkode": "RV_OV_1"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_OV_2', '{"kode": "RV_OV_2", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "c49ecb0f-97dd-4967-bd08-af0c616ff2cf", "tittel": "Opptjeningsvurdering må gjøres manuelt fordi opplysningene fra AA-registeret er ufullstendige", "avviklet": true, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_OV_2"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_MV_1', '{"kode": "RV_MV_1", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "b8ccbad1-de8c-4ca6-b944-df240bc42e6b", "tittel": "Vurder lovvalg og medlemskap", "avviklet": false, "handling": "Sjekk om personen er medlem i folketrygden – se i medlemskapsfanen i Gosys. Sjekk bostedsadressen og hvor arbeidet utføres. Hvis bokommune er 0393 eller 2101 skal saken avvises og behandles av NAV Utland. Kilder: Gosys og Aa-registeret.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Medlemskapssjekken har ikke klart å entydig konkludere med at bruker er medlem", "varselkode": "RV_MV_1"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_MV_2', '{"kode": "RV_MV_2", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "ed84657a-f3e5-42f1-9d04-b8116c08dbf3", "tittel": "Perioden er avslått på grunn av at den sykmeldte ikke er medlem av Folketrygden", "avviklet": false, "handling": "Undersøk om det er riktig at personen ikke er medlem av Folketrygden. Hvis personen ikke er medlem kan saken godkjennes i speil og det må sendes vedtak i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Alle dager er avvist på grunn av at personen ikke er medlem av Folketrygden", "varselkode": "RV_MV_2"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IV_1', '{"kode": "RV_IV_1", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "a52b7dca-d12d-4b5b-90ae-b8eac1381557", "tittel": "Bruker har flere inntektskilder de siste tre månedene enn arbeidsforhold som er oppdaget i Aa-registeret.", "avviklet": false, "handling": "Sjekk om bruker er kombinert arbeidstaker og frilanser. Hvis ja, send saken til Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Bruker kan være frilanser.", "varselkode": "RV_IV_1"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IV_2', '{"kode": "RV_IV_2", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "24daf321-bd10-4dcc-a6a8-4f7fc81c0bb3", "tittel": "Har mer enn 25 % avvik. Dette støttes foreløpig ikke i Speil. Du må derfor annullere periodene.", "avviklet": false, "handling": "Du kan endre inntekten en gang til hvis du har gjort noe feil. Dersom endringen skal gjennomføres må periodene annulleres i Speil og behandles i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Det er lagt inn en ny inntekt som gir 25 % avvik. Skjønnsmessig fastsettelse av sykepengegrunnlaget støttes ikke i Speil.", "varselkode": "RV_IV_2"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IV_3', '{"kode": "RV_IV_3", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "afe863cc-6794-444e-99d0-8d666f095456", "tittel": "Fant frilanserinntekt på en arbeidsgiver de siste 3 månedene", "avviklet": false, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_IV_3"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IV_4', '{"kode": "RV_IV_4", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "4254de6b-a250-4733-ab16-44333a13a7e9", "tittel": "Finnes inntekter fra flere virksomheter siste tre måneder", "avviklet": true, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_IV_4"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IV_5', '{"kode": "RV_IV_5", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "8b4d6eaa-e733-4171-ab28-cf5cfe20f560", "tittel": "Har inntekt på flere arbeidsgivere med forskjellig fom dato", "avviklet": true, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_IV_5"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_IV_6', '{"kode": "RV_IV_6", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "96a60761-f519-4114-8781-e6c9c63fa4ad", "tittel": "Inntekter fra mer enn én arbeidsgiver i A-ordningen siste tre måneder. Kontroller om brukeren har flere arbeidsforhold på sykmeldingstidspunktet. Flere arbeidsforhold støttes ikke av systemet.", "avviklet": true, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_IV_6"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SV_1', '{"kode": "RV_SV_1", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "e8725819-815e-413b-982f-5fb17317b4dd", "tittel": "Perioden er avslått på grunn av at inntekt er under krav til minste sykepengegrunnlag", "avviklet": false, "handling": "Undersøk at sykepengegrunnlaget er korrekt. Hvis det er fastsatt korrekt kan saken godkjennes i speil, da må avslagsbrev sendes i infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Alle dager er avvist på grunn av at sykepengegrunnlaget er under minstekrav", "varselkode": "RV_SV_1"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SV_2', '{"kode": "RV_SV_2", "opprettet": "2022-11-30T18:20:47.245724", "definisjoner": [{"id": "a278c540-345e-4430-8a96-1ce3af4103ce", "tittel": "Minst en arbeidsgiver inngår ikke i sykepengegrunnlaget", "avviklet": false, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_SV_2"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SV_3', '{"kode": "RV_SV_3", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "bd9c5793-5248-4135-98c2-4dd730add09f", "tittel": "Mangler inntekt for sykepengegrunnlag som følge av at skjæringstidspunktet har endret seg", "avviklet": false, "handling": null, "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": null, "varselkode": "RV_SV_3"}]}') ON CONFLICT(varselkode) DO NOTHING;
INSERT INTO varseldefinisjon (varselkode, json) VALUES ('RV_SV_4', '{"kode": "RV_SV_4", "opprettet": "2022-11-30T18:25:03.355", "definisjoner": [{"id": "04b884a8-5320-415c-a223-9f80e778d95b", "tittel": "Brukeren har flere inntekter de siste tre måneder. Kontroller om brukeren har flere arbeidsforhold eller andre ytelser på sykmeldingstidspunktet som påvirker utbetalingen.", "avviklet": true, "handling": "Sjekk om den sykmeldte har flere aktive inntektskilder på sykmeldingstidspunktet. Speil håndterer ikke kombinasjonen arbeidstaker og frilanser. I slike tilfeller må saken behandles i Infotrygd.", "opprettet": "2022-11-30T18:25:03.355", "forfattere": [], "forklaring": "Det finnes inntekter fra mer enn én arbeidsgiver i A-ordningen i de siste tre månedene", "varselkode": "RV_SV_4"}]}') ON CONFLICT(varselkode) DO NOTHING;
